<template>
    <ion-page>
        <Header/>
        <ion-content :fullScreen="true">
          <div class="bg-purple-600" v-if="showDonationInfo">
            <div class="max-w-7xl mx-auto py-3 px-3 sm:px-6 lg:px-8">
              <div class="flex items-center justify-between flex-wrap">
                <div class="w-0 flex-1 flex items-center">
                  <span class="flex p-2 rounded-lg bg-indigo-800">
                    <!-- Heroicon name: outline/speakerphone -->
                    <svg class="h-6 w-6 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M11 5.882V19.24a1.76 1.76 0 01-3.417.592l-2.147-6.15M18 13a3 3 0 100-6M5.436 13.683A4.001 4.001 0 017 6h1.832c4.1 0 7.625-1.234 9.168-3v14c-1.543-1.766-5.067-3-9.168-3H7a3.988 3.988 0 01-1.564-.317z" />
                    </svg>
                  </span>
                  <p class="ml-3 font-medium text-white truncate">
                    <span class="md:hidden">
                      Soutenez nous ! 
                    </span>
                  </p>
                </div>
                <div class="order-3 mt-2 flex-shrink-0 w-full sm:order-2 sm:mt-0 sm:w-auto">
                  <a @click="openDonation" class="flex items-center justify-center px-4 py-2 border border-transparent rounded-md shadow-sm text-base font-bold text-purple-600 bg-white hover:bg-purple-50">
                    Faire un don
                  </a>
                </div>
                <div class="order-2 flex-shrink-0 sm:order-3 sm:ml-3">
                  <button @click="closeDonationInfo" type="button" class="-mr-1 flex p-2 rounded-md hover:bg-indigo-500 focus:outline-none focus:ring-2 focus:ring-white sm:-mr-2">
                    <!-- Heroicon name: outline/x -->
                    <svg class="h-6 w-6 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
                      <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>
              </div>
            </div>
          </div>
          <div>
            <div class="flex w-full">
              <div class="rounded-full h-30 w-24 bg-center bg-cover mt-3 ml-3" id="profil">
              </div>
              <div class="flex-grow px-1 py-3">
                  <div class="flex justify-center">
                    <div class="w-1/12"></div>
                    <div class="w-10/12 text-center">
                      <span class="text-xl inline-block bg-yellow-500 text-black font-bold py-1 px-3 rounded-lg ">
                        <svg width="23" height="30" viewBox="0 0 23 22" fill="none" xmlns="http://www.w3.org/2000/svg" class="inline-block">
